mapper.select:
SELECT c.cart_id AS cartId, c.user_id AS userId, c.item_id AS itemId, c.item_option_id AS itemOptionId, c.quantity, c.added_at AS addedAt, c.created_at AS createdAt, c.updated_at AS updatedAt,
i.name AS itemName, i.image AS itemImage, i.price AS price,
color.value AS colorOptionName, size.value AS sizeOptionName, io.additional_price AS additionalPrice FROM cart c JOIN item_option io ON c.item_option_id = io.item_option_id AND c.item_id = io.item_id JOIN item i ON c.item_id = i.item_id JOIN `option` color ON io.color_option_id = color.option_id JOIN `option` size ON io.size_option_id = size.option_id WHERE c.user_id = #{userId}

mapper.update:
UPDATE cart
SET quantity = #{itemQuantity}, updated_at = NOW()
WHERE cart_id = #{cartId}

UPDATE cart
SET quantity = #{quantity}, updated_at = NOW()
WHERE cart_id = #{cartId}
